package slidingwindow;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

public class Anagram {
	
	public static void main(String[] args) {
		Anagram obj = new Anagram();
		String str = "";
		String txt = "";
		System.out.println(obj.search("forxforfxdofr", "for"));
		//System.out.println(obj.search(str, txt));
		System.out.println(obj.findAnagrams("cbaebabacd","abc").size());
	        
	}
	
	public List<Integer> findAnagrams(String s, String p) {
	    List<Integer> list = new ArrayList<>();
	    if (s == null || s.length() == 0 || p == null || p.length() == 0) return list;
	    int[] hash = new int[256]; //character hash
	    //record each character in p to hash
	    for (char c : p.toCharArray()) {
	        hash[c]++;
	    }
	    //two points, initialize count to p's length
	    int left = 0, right = 0, count = p.length();
	    while (right < s.length()) {
	        //move right everytime, if the character exists in p's hash, decrease the count
	        //current hash value >= 1 means the character is existing in p
	        if (hash[s.charAt(right++)]-- >= 1) count--; 
	        
	        //when the count is down to 0, means we found the right anagram
	        //then add window's left to result list
	        if (count == 0) list.add(left);
	    
	        //if we find the window's size equals to p, then we have to move left (narrow the window) to find the new match window
	        //++ to reset the hash because we kicked out the left
	        //only increase the count if the character is in p
	        //the count >= 0 indicate it was original in the hash, cuz it won't go below 0
	        if (right - left == p.length() && hash[s.charAt(left++)]++ >= 0) count++;
	    }
	    return list;
	}

	int search(String pat, String txt) {

		int count = 0;
		int i = 0;
		int j = 0;

		Map<Character, Integer> inputMap = new HashMap<>();
		
		for (Character ch : txt.toCharArray()) {
			inputMap.put(ch, (inputMap.get(ch)==null?0:inputMap.get(ch)) + 1);
		}

		Map<Character, Integer> map = new HashMap<>();

		while (j < pat.length()) {
			// constrct temp map;
			char ch = pat.charAt(j);
			map.put(ch, (map.get(ch)==null?0:map.get(ch)) + 1);
			
			if (j - i + 1 == txt.length()) {
				if (anagram(inputMap, map)) {
					count++;
				}
				char ch1 = pat.charAt(i);
				map.put(ch1, map.get(ch1) - 1);
				i++;
			}
			j++;
		}
		return count;
	}

	private boolean anagram(Map<Character, Integer> map1, Map<Character, Integer> map2) {
		for (Character ch : map1.keySet()) {
			if (!map1.get(ch).equals(map2.get(ch)))
				return false;
		}
		return true;
	}

}
